        <script>
        	var app_url = '{{env('APP_URL')}}';
            // Create a Stripe client.
            var stripe = Stripe('{{env('STRIPE_PUBLIC_KEY')}}');
            var user_id = {{$user->id}};

            var elements = stripe.elements();
            var cardElement = elements.create('cardNumber', {
                showIcon: true,
                iconStyle: 'solid',
                placeholder: 'ENTER CARD NUMBER',
                style: {
                  base: {
                    iconColor: '#424242',
                    color: '#424242',
                    fontWeight: 400,
                    fontFamily: 'Roboto, sans-serif',
                    fontSize: '14px',
                    fontSmoothing: 'antialiased',

                    ':-webkit-autofill': {
                      color: '#424242',
                    },
                    '::placeholder': {
                      color: '#424242',
                    },
                  },
                  invalid: {
                    iconColor: 'red',
                    color: 'red',
                  },
                },
              });
            cardElement.mount('#card-number');
            var cardElement = elements.create('cardExpiry', {
                style: {
                  base: {
                    iconColor: '#424242',
                    color: '#424242',
                    fontWeight: 400,
                    fontFamily: 'Roboto, sans-serif',
                    fontSize: '14px',
                    fontSmoothing: 'antialiased',

                    ':-webkit-autofill': {
                      color: '#424242',
                    },
                    '::placeholder': {
                      color: '#424242',
                    },
                  },
                  invalid: {
                    iconColor: 'red',
                    color: 'red',
                  },
                },
              });
            cardElement.mount('#card-expiry');
            var cardElement = elements.create('cardCvc', {
                style: {
                  base: {
                    iconColor: '#424242',
                    color: '#424242',
                    fontWeight: 400,
                    fontFamily: 'Roboto, sans-serif',
                    fontSize: '14px',
                    fontSmoothing: 'antialiased',

                    ':-webkit-autofill': {
                      color: '#424242',
                    },
                    '::placeholder': {
                      color: '#424242',
                    },
                  },
                  invalid: {
                    iconColor: 'red',
                    color: 'red',
                  },
                },
              });
            cardElement.mount('#card-cvc');

			var cardButton = document.getElementById('card-button');
			var clientSecret = cardButton.dataset.secret;

			cardButton.addEventListener('click', function(ev) {

				// disable the button
				$('#card-button').attr('disabled', true);
				// hide error message if any shown
				$('.error').hide();

			  stripe.confirmCardSetup(
			    clientSecret,
			    {
			      payment_method: {
			        card: cardElement,
			      }
			    }
			  ).then(function(result) {
			  	console.log(result);
			    if (result.error) {
			      // Display error.message in your UI.
			      //window.location = '{{$STRIPE_FAILED_PAYMENT_URL}}';
			      // show custom error message
			      $('.error').show();
			      //enable the button again
			      $('#card-button').removeAttr('disabled');
			    } else {
			      // The setup has succeeded. Display a success message.
			      $(".se-pre-con").fadeIn();
                  $.post(app_url+'/updatecard', {'payment_method': result.setupIntent.payment_method, 'user_id': user_id, '_token': '{{ csrf_token() }}'}).done(function(success){
                    $(".se-pre-con").fadeOut();
                        console.log(success);
                        window.location = '{{$STRIPE_SUCCESS_PAYMENT_URL}}';
                  }).catch(function(error){
                        console.log(error);
                        $(".se-pre-con").fadeOut();
                        // error
                        window.location = '{{$STRIPE_FAILED_PAYMENT_URL}}';                   
                  });    
			    }
			  });
			});
        </script>

        
    </body>
